//Back-end complete function template in C++

class Solution {
    public:

    //Function to find the Nth term of a sequence.
    int NthTerm(int n){

        //if n is 0, return 0.
        if(n == 0)
            return 0;

        long long int ans = 1;
        long long int mod = 1e9 + 7;

        //iterating from 1 to n to calculate the factorial.
        for(int i = 1; i <= n; i++){
            ans = ans * i;
            ans %= mod;
        }

        //multiplying the factorial with n and taking modulo.
        ans *= n;
        ans %= mod;

        return ans;
    }
};
