class Solution {
  public:
    long double convertToDecimal(string n) {
        
        int pos=n.length();
        
        // Find the position of '.' first
        for(int i=0 ; i<n.length() ; i++)
        {
            if(n[i]=='.')
            {
                pos=i;
                break;
            }
        }
        
        long double intDecimal = 0.0, fracDecimal = 0.0, twos = 1.0;

    	// Convert integral part of binary to decimal
    	// equivalent
    	for (int i = pos-1; i>=0; --i)
    	{
    		// Subtract '0' to convert character
    		// into integer
    		intDecimal += (n[i] - '0') * twos;
    		twos *= 2.0;
    	}
    
    	// Convert fractional part of binary to
    	// decimal equivalent
    	twos = 2.0;
    	for (int i = pos+1; i < n.length(); ++i)
    	{
    	   // cout<<(double)((binary[i] - '0') / twos)<<endl;
    		fracDecimal += (double)((n[i] - '0') / twos);
    		twos *= 2.0;
    	}
    	
    	// Add both integral and fractional part
	
	    return (intDecimal) +(fracDecimal);
    }
};

java.full_func:
class Solution {
    static double convertToDecimal(String n) {
        // code here
        int len=n.length();
        int in=0;
        for(int i=0;i<len;i++){
            if(n.charAt(i)=='.'){
                in=i;
                break;
            }
        }
        double ans=0;
        int val=0;
        for(int i=in-1;i>=0;i--){
            if(n.charAt(i)=='1')
            ans+=Math.pow(2,val);
            val++;
        }
        val=1;
        for(int i=in+1;i<len;i++){
            if(n.charAt(i)=='1')
            ans+=1.0/Math.pow(2,val);
            val++;
        }
        return ans;
    }
};
