#include <iostream>
using namespace std;

int main() {
    int B;
    cin >> B;
    int ans = 0;
    int temp = B;
    int base = 1;
    while (temp > 0) {
        int lastDigit = temp % 10;
        ans += (lastDigit * base);
        base = base * 2;
        temp = temp / 10;
    }
    cout << ans;

    return 0;
}
