//Back-end complete function Template for C

int binary_to_decimal(int B)
{
	int ans = 0;
	int temp = B;
	int base = 1;
	while(temp > 0){
	    int lastDigit = temp % 10;
	    ans += (lastDigit * base);
	    base = base * 2;
	    temp = temp / 10;
	}
	return ans;
}

//Back-end complete function Template for C++

class Solution {
  public:
    int binary_to_decimal(int B) {
        int ans = 0;
        int temp = B;
        int base = 1;
        while (temp > 0) {
            int lastDigit = temp % 10;
            ans += (lastDigit * base);
            base = base * 2;
            temp = temp / 10;
        }
        return ans;
    }
};

