#include <stdio.h>

int main() {
    int a, b, c;
    scanf("%d %d %d", &a, &b, &c);

    // perform the mentioned bitwise operations,
    // and print d, e, f, g in the same line
    // code here
    int d = a ^ a;
    int e = c ^ b;
    int f = a & b;
    int g = c | (a ^ a);
    e = ~e;

    printf("%d %d %d %d\n", d, e, f, g);

    return 0;
}

