// Function to find the boundary elements of a matrix
class Solution {
public:
    vector<int> BoundaryElements(vector<vector<int>>& matrix) {
        vector<int> ans; // Initialize an empty vector to store the boundary elements
        int n = matrix.size(); // Get the size of the matrix

        // Loop through each element in the matrix
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                // Check if the current element is on the boundary
                if (i == 0 or i == n-1 or j == 0 or j == n-1)
                    ans.push_back(matrix[i][j]); // If yes, add it to the vector
            }
        }
        return ans; // Return the vector of boundary elements
    }
};
