// backend complete function template for C++

class Solution {
  public:
    int BasicDataType(string S) {
        int l = S.length(), ans;
        if (l == 1) {
            //checking if S is integer of size 1
            if (isdigit(S[0]))
                ans = 4;
            else
                ans = 1;
        } else {
            int flag = 0;
            for (int i = 0; i < l; i++) {
                //checking whether S is integer or float/double
                if (S[i] == '.') {
                    flag = 1;
                    int j = l - i + 1;
                    //checking whether S is double
                    if (j > 7)
                        ans = 8;
                    else
                        ans = 4;
                }
            }
            if (!flag) ans = 4;
        }
        return ans;
    }
};

class Solution
{
    //Complete this function
    public int BasicDataType(string s)
    {
        int l = s.Length;
        int ans = 0; // Assign a default value to ans
        if (l == 1)
        {
            //checking if s is integer of size 1
            if (char.IsDigit(s[0]))
                ans = 4;
            else
                ans = 1;
        }
        else
        {
            int flag = 0;
            for (int i = 0; i < l; i++)
            {
                //checking whether s is integer or float/double
                if (s[i] == '.')
                {
                    flag = 1;
                    int j = l - i + 1;
                    //checking whether s is double
                    if (j > 7)
                        ans = 8;
                    else
                        ans = 4;
                }
            }
            if (flag == 0)
                ans = 4;
        }
        return ans;
    }
}
