//Back-end complete function Template for C++

class complex
{
    int x, y; //declaration of private variables

    public:
    complex(){} //default constructor

    complex(int real, int imag) //parameterized constructor
    {x=real; y=imag;} //initialize x and y with the given values

    complex operator+(complex c) //overloading '+' operator
    {
        complex t; //create a new object t
        t.x = x+ c.x; //add the real parts of the current object and the passed object
        t.y = y+ c.y; //add the imaginary parts of the current object and the passed object
        return(t); //return the new object t
    }

    void display() //display function
    {
        cout<<x<<" + "<<y<<"i"<<endl; //print the complex number in the format x + yi
    }
};
