//Back-end complete function template in C++

class Solution{
    public:
    //Function to count the number of same characters at same positions in two strings.
    int sameChar(string A, string B)
    {
        int count=0;

        //iterating over each character in string B.
        for(int i=0; i<B.length(); i++)
        {
            //converting both characters to lowercase if they are uppercase.
            if(isupper(A[i]))
                A[i] = tolower(A[i]);
            if(isupper(B[i]))
                B[i] = tolower(B[i]);

            //increasing count if both characters are equal.
            if(A[i] == B[i])
                count++;
        }
        //returning the count of same characters at same positions.
        return count;
    }
};

//Back-end complete function Template for Java
class Solution 
{ 
    // Function to count the number of same characters at the same index in two strings
    int sameChar(String A, String B) 
    {
        // Variable to store the count of same characters
        int ans=0;

        // Loop through each character in the strings
        for(int i=0;i<A.length();i++){
            // Get the character at the i-th index in both strings
            char x=A.charAt(i);
            char y=B.charAt(i);

            // Convert uppercase letters to lowercase letters for comparison
            if(x>='A'&&x<='Z')
                x=(char)(x-'A'+'a');
            if(y>='A'&&y<='Z')
                y=(char)(y-'A'+'a');

            // If characters are the same, increment the count
            if(x==y)
                ans++;
        }

        // Return the count of same characters
        return ans;
    }
}

//User function Template for C#

class Solution
{
    //Complete this function
    //Function to count the number of strings with the same characters.
    public int sameChar(string A, string B)
    {
        int count=0;

        //iterating over each character in string B.
        for(int i=0; i<A.Length; i++)
        {
            //converting both characters to lowercase if they are uppercase.
            if(char.IsUpper(A[i]))
                A = A.Remove(i, 1).Insert(i, char.ToLower(A[i]).ToString());
            if(char.IsUpper(B[i]))
                B = B.Remove(i, 1).Insert(i, char.ToLower(B[i]).ToString());

            //increasing count if both characters are equal.
            if(A[i] == B[i])
                count++;
        }
        //returning the count of same characters at same positions.
        return count;
    }
}
