//Back-end complete function template in C++

class Solution {
  public:
    //Function to take input and return an integer.
    int cppIntType() {
        int A;
        cin>>A;
        return A;
    }

    //Function to take input and return a character.
    char cppCharType() {
        char B;
        cin>>B;
        return B;
    }

    //Function to take input and return a float.
    float cppFloatType() {
        float C;
        cin>>C;
        return C;
    }
};
