//Back-end complete function Template for C++

class Cuboid
{
    public:
    //Attributes to store the length, width, and height of the cuboid
    int length, width, height;

    //Function to display the dimensions of the cuboid
    void display()
    {
        cout<<length<<" "<<width<<" "<<height<<endl;
    }


};

class CuboidVol: public Cuboid
{
    public:
    //Function to read and assign the input values to the dimensions of the cuboid
    void read_input(int l, int w, int h)
    {
        length = l;
        width = w;
        height = h;

    }

    //Function to calculate and display the volume of the cuboid
    void display()
    {
        cout<<length * width * height<<endl;
    }

};
