//Back-end complete function Template for C++
class test: public student 
{
    protected:
        float sub[5];

    public:
        void set_marks(float temp[5])
        {
            for(int i = 0; i < 5; i++)
            sub[i] = temp[i];
        }

        void display_marks()
        {
            for(int i = 0; i < 5; i++)
            cout<<sub[i];
        }
};

class result: public test
{
    protected:
    float total = 0;
    float average = 0;

    public:
    void display()
    {
        for(int i = 0; i < 5; i++)
        total = total + sub[i];
        cout<<roll_number<<" "<<total<<" ";
        average = total / 5;
        cout<<average<<endl;
    }

};
