// Function to update the value of a variable by adding 10 to it
void updateVar(int *a) {
    *a = *a + 10; // Updating the value of the variable by adding 10 to it
}

// Function to update the value of a variable by adding 10 to it
void updateVar(int *a)
{
    // Adding 10 to the value pointed by "a"
    *a = *a + 10;
}

class Solution
{
    //Complete this function
    public void updateVar(ref int a)
    {
        // Adding 10 to the value pointed by "a"
        a = a + 10;
    }
}
