// Function to add a pair to the vector A
// Parameters: A - vector of pairs, str - string value, x and y - integer values
void add_pair(vector<pair<string,pair<int,int> > > &A, string str, int x, int y)
{
     A.push_back(make_pair(str,make_pair(x,y)));
}

// Function to get the size of vector A
// Parameters: A - vector of pairs
// Returns: size of vector A
int get_size(vector<pair<string,pair<int,int> > > &A)
{
     return A.size();
}

// Function to print the values in vector A
// Parameters: A - vector of pairs
void print_values(vector<pair<string,pair<int,int> > > &A)
{
    for(int i=0; i<A.size(); i++)
        cout<<A[i].first<<" "<<A[i].second.first<<" "<<A[i].second.second<<" ";
}

// Comparison function for sorting the vector A based on the values of x and y
// Parameters: a and b - pairs to be compared
// Returns: boolean value indicating whether a is less than b based on x and y values
bool cmp(pair<string,pair<int,int>> a, pair<string,pair<int,int>> b)
{
    if(a.second.first < b.second.first)
        return 1;
    if(a.second.first == b.second.first && a.second.second < b.second.second)
        return 1;
    else
        return 0;
}

// Function to sort the vector A based on the values of x and y
// Parameters: A - vector of pairs
void sort_pair(vector<pair<string,pair<int,int> > > &A)
{
    sort(A.begin(),A.end(),cmp);
}
