// Function to insert an element into the unordered set
void insert(unordered_set<int> &s,int x)
{
    s.insert(x);
}

// Function to erase an element from the unordered set
void erase(unordered_set<int> &s,int x)
{
    // Finding the element in the set
    std::unordered_set<int>::iterator it;
    it = s.find(x);

    // Erasing the element if found
    if(it!=s.end())
    {
        s.erase(it);
    }
}

// Function to get the size of the unordered set
int size(unordered_set<int> &s)
{
    return s.size();
}

// Function to find an element in the unordered set
int find(unordered_set<int> &s,int x)
{
    // Finding the element in the set
    std::unordered_set<int>::iterator it;
    it = s.find(x);

    // Returning 1 if element is found, -1 otherwise
    if(it!=s.end())
    {
        return 1;
    }
    else 
    {
        return -1;
    }
}

