template <class T>
//Class template for finding the minimum element
class minElement
{
    T element; 
    public:
    //Default constructor
    minElement(){};
    //Parameterized constructor
    minElement(T a){
        element = a;
    }
    //Function to check and output the minimum of two elements
    void check(T x)
    {
    cout<<(element<x ? element : x)<<endl;
    }   
};
