#include <iostream>
using namespace std;

int main() {
    int a, b, operator_;
    cin >> a >> b >> operator_;
    if (operator_ == 1) {
        cout << (a + b);
    } else if (operator_ == 2) {
        cout << (a - b);
    } else if (operator_ == 3) {
        cout << (a * b);
    } else {
        cout << ("Invalid Input");
    }
    cout << endl;

    return 0;
}

