//Back-end complete function Template for Java
import java.util.*;

public class Main {
    public static void main(String args[]) {
        Scanner sc = new Scanner(System.in);
        int a = sc.nextInt();
        int b = sc.nextInt();
        int operator = sc.nextInt();
        if (operator == 1) {
            System.out.println(a + b);
        } else if (operator == 2) {
            System.out.println(a - b);
        } else if (operator == 3) {
            System.out.println(a * b);
        } else {
            System.out.println("Invalid Input");
        }
    }
}

