//Back-end complete function Template for javascript
class Solution {
    utility(a, b, operator) {
        switch (operator) {
            case 1:
                process.stdout.write((a + b).toString());
                break;
            case 2:
                process.stdout.write((a - b).toString());
                break;
            case 3:
                process.stdout.write((a * b).toString());
                break;
            default:
                process.stdout.write("Invalid Input");
        }
    }
}
