cpp.full_func:
//Back-end complete function template in C++

class Solution {
  public:
    //Function to calculate the Nth Carol number.
    int nthCarol(int N) {

        int ans;

        //Calculating the result using the formula 2^N - 1.
        int result = pow(2, N) - 1;

        //Calculating the Nth Carol number using the formula result^2 - 2.
        ans = result*result - 2;

        //Returning the Nth Carol number.
        return ans;
    }
};

java.full_func:
// Back-end complete function Template for Java

class Solution {
    // Function to calculate the nth Carol number.
    static int nthCarol(int N) {

        int ans; // Variable to store the answer.

        // Calculating the result using the formula (2^N - 1)^2 - 2.
        int result = (int)Math.pow(2, N) - 1;
        ans = result*result - 2;

        // Return the calculated answer.
        return ans;
    }
};

