// Back-end complete function template for C++

class Solution{   
public:
    // Function to calculate the power of a number with mod
    long long power(long long x, long long y, long long mod)
    {
        if (y==0)
            return 1;
        long long temp = power(x, y/2, mod) % mod;
        temp = (temp*temp) % mod;
        if (y%2==1)
            temp = (temp*x) % mod;
        return temp;
    }

    // Function to calculate the nth Carol number
    long long carol(long long n)
    {   
        long long MOD=1e9+7;
        long long result = power(2, n,MOD) - 1;
        return ((result*result)%MOD - 2)%MOD;
    }

    // Function to find the nth Carol number
    long long nthCarolNumber(long long n){
        return carol(n);
    }
};
