// Function to get floor and ceil of a given value in an array
class Solution {
  public:
    vector<int> getFloorAndCeil(int x, vector<int> arr) {
        int n = arr.size();
        int floorDiff = INT_MAX, ceilDiff = INT_MAX;
        int ceilVal = -1, floorVal = -1;

        // Looping through the array to find the floor and ceil of x
        for (int i = 0; i < n; i++) {
            // Updating the ceil if the current element is greater than or equal to x
            // and the difference is smaller
            if (arr[i] >= x && ceilDiff > (arr[i] - x)) {
                ceilDiff = arr[i] - x;
                ceilVal = arr[i];
            }
            // Updating the floor if the current element is smaller than or equal to x
            // and the difference is smaller
            if (arr[i] <= x && floorDiff > (x - arr[i])) {
                floorDiff = x - arr[i];
                floorVal = arr[i];
            }
        }

        return {floorVal, ceilVal};
    }
};

class Solution {
    public int[] getFloorAndCeil(int x, int[] arr) {
        int floorVal = -1, ceilVal = -1;
        int floorDiff = Integer.MAX_VALUE, ceilDiff = Integer.MAX_VALUE;

        // Looping through the array to find the floor and ceil of x
        for (int num : arr) {
            // Updating the ceil if the current element is greater than or equal to x
            // and the difference is smaller
            if (num >= x && ceilDiff > (num - x)) {
                ceilDiff = num - x;
                ceilVal = num;
            }
            // Updating the floor if the current element is smaller than or equal to x
            // and the difference is smaller
            if (num <= x && floorDiff > (x - num)) {
                floorDiff = x - num;
                floorVal = num;
            }
        }

        return new int[] {floorVal, ceilVal};
    }
}

