class Solution {
  public:
    // Function to convert all even bits of a number to 0
    long long int convertEvenBitToZero(long long int n) {
    return (n & 0xaaaaaaaa); // bitwise AND operation to convert even bits to 0
    } 
};

public class Solution{
    public long convertEvenBitToZero(long n){
        return (n & 0xaaaaaaaa);
    }
}
