/**
 * @param {number} N
 * @return {number[]}
 */
class Solution {
    changeBits(N) {
        let arr = [];
        let bits = Math.floor(Math.log2(N)) + 1; 
        let ans = (1 << bits) - 1;
        let sub = ans - N;     
        arr[0] = sub;
        arr[1] = ans;
        return arr;
    }
}
