//Back-end complete function Template for Java

public static void changeCase(String s){
    System.out.println(Character.toUpperCase(s.charAt(0)) + s.substring(1));
    System.out.println(s.toUpperCase());
}

//User function Template for javascript
/**
 * @param {string} s
 */

class Solution {
    changeCase(s) {
        console.log(s.charAt(0).toUpperCase() + s.slice(1)); // print first letter capitalized
        console.log(s.toUpperCase()); // print the entire string in upper case
    }
}
