class Solution {
  public:

    string modify(string s) {
        // check if the string has to be made lowercase of uppercase
        bool upper;
        if (s[0] >= 'A' and s[0] <= 'Z')
            upper = true;
        else
            upper = false;

        for (int i = 1; i < s.length(); ++i) {
            if (upper) {
                // converting to uppercase
                if (s[i] >= 'a' and s[i] <= 'z')
                    s[i] = s[i] - 'a' + 'A';
            } else {
                // converting to lowercase
                if (s[i] >= 'A' and s[i] <= 'Z')
                    s[i] = s[i] - 'A' + 'a';
            }
        }
        return s;
    }
};

