class Solution
{
    //Complete this function
    public string modify(string s)
    {
        bool upper;
        if (s[0] >= 'A' && s[0] <= 'Z')
            upper = true;
        else
            upper = false;

        for (int i = 1; i < s.Length; ++i)
        {
            if (upper)
            {
                // converting to uppercase
                if (s[i] >= 'a' && s[i] <= 'z')
                    s = s.Substring(0, i) + (char)(s[i] - 'a' + 'A') + s.Substring(i + 1);
            }
            else
            {
                // converting to lowercase
                if (s[i] >= 'A' && s[i] <= 'Z')
                    s = s.Substring(0, i) + (char)(s[i] - 'A' + 'a') + s.Substring(i + 1);
            }
        }
        return s;
    }

}

