/**
 * @param {string} s
 * @returns {string}
*/

class Solution{
    modify(s){
        let upper = s[0] >= 'A' && s[0] <= 'Z';

        for (let i = 1; i < s.length; i++) {
            if (upper) {
                if (s[i] >= 'a' && s[i] <= 'z') {
                    s = s.substring(0, i) + String.fromCharCode(s.charCodeAt(i) - 32) + s.substring(i + 1);
                }
            } else {
                if (s[i] >= 'A' && s[i] <= 'Z') {
                    s = s.substring(0, i) + String.fromCharCode(s.charCodeAt(i) + 32) + s.substring(i + 1);
                }
            }
        }

        return s;
    }
}
