class Solution{   
public:
    // Function to check if the given number is a power of 3 or not
    string isPowerof3(int N){
        /* The maximum power of 3 value that 
           integer can hold is 1162261467 ( 3^19 ).*/

        // Check if N is divisible by the maximum power of 3
        if(1162261467 % N == 0)
            return "Yes";
        else 
            return "No";

    }
};

