class Solution {
    // Function to check if the given string is binary or not
    boolean isBinary(String str) {
        // Traverse through each character in the string
        for (int i = 0; i < str.length(); i++) {
            // Check if the character is not '0' or '1'
            if (str.charAt(i) != '0' && str.charAt(i) != '1')
                // Return false if the character is not binary
                return false;
        }
        // Return true if all characters are binary
        return true;
    }
}

