//Back-end complete function Template for C++
class Solution {
  public:
    vector<int>consecutiveSum(int n) { 
    vector<int>v;
    // if n is multiple of 3 
    if (n % 3 == 0){
        v.push_back(n / 3 - 1); 
        v.push_back(n / 3 );
        v.push_back(n / 3 + 1);
    } 
    // else print "-1". 
    else
        v.push_back(-1); 
    return v;
} 
};
