class Solution{
    int DivisibleByEight(String s){
        int len=s.length(),ans; //variable to store the length of the string and the result

        if(len>3){
            int number=0; //variable to store the last 3 digits as a number
            for(int i=len-3;i<len;i++)
             number=number*10+(int)(s.charAt(i)-'0'); //converting the last 3 digits to a number

            if(number%8==0)
             ans=1; //if the number is divisible by 8, set the result to 1
            else 
             ans=-1; //else set the result to -1
        }
        else{
            int number=0; //variable to store the number
            for(int i=0;i<len;i++)
             number=number*10+(int)(s.charAt(i)-'0'); //converting the entire string to a number

            if(number%8==0)
             ans=1; //if the number is divisible by 8, set the result to 1
            else 
             ans=-1; //else set the result to -1
        }
        return ans; //return the result
    }
}

