class Solution {
  public:
    int isPowerOfAnother(int X, int Y) {

        // The only power of 1 is 1 itself
        if (X == 1) {
            if (Y == 1)
                return 1;
            else
                return 0;
        }
        // Repeatedly comput power of x
        long int pow = 1;
        while (pow < Y)
            pow *= X;

        // Check if power of x becomes y
        if (pow == Y)
            return 1;
        else
            return 0;
    }
};

class Solution {
    public boolean isPowerOfAnother(int X, int Y) {
        // The only power of 1 is 1 itself
        if (X == 1) {
            return Y == 1;
        }

        int power = 1;
        while (power < Y) {
            power *= X;
        }

        return power == Y;
    }
}

