class Solution {
  public:
    // Function to check if the binary number is divisible by 10
    int isDivisibleBy10(string bin) {
        int n = bin.size();
        if (bin[n - 1] == '1') return 0;
        int sum = 0;
        // Iterate through the binary number from right to left
        for (int i = n - 2; i >= 0; i--) {
            // If the digit is 1
            if (bin[i] == '1') {
                int posFromRight = n - i - 1;
                // Determine the position of the digit from the right and add the corresponding value to the sum
                if (posFromRight % 4 == 1)
                    sum = sum + 2;
                else if (posFromRight % 4 == 2)
                    sum = sum + 4;
                else if (posFromRight % 4 == 3)
                    sum = sum + 8;
                else if (posFromRight % 4 == 0)
                    sum = sum + 6;
            }
        }
        // Check if the sum is divisible by 10
        if (sum % 10 == 0) return 1;
        return 0;
    }
};

