    int isDivisibleBy10(String bin) {
        int n = bin.length();
        // Check if the last digit is 1, if yes return 0
        if (bin.charAt(n - 1) == '1') return 0;
        int sum = 0;
        // Iterate through the remaining digits starting from the second last digit
        for (int i = n - 2; i >= 0; i--) {
            // Check if the digit is 1
            if (bin.charAt(i) == '1') {
                int posFromRight = n - i - 1;
                // Add the corresponding value to the sum based on the position from the right
                if (posFromRight % 4 == 1)
                    sum = sum + 2;
                else if (posFromRight % 4 == 2)
                    sum = sum + 4;
                else if (posFromRight % 4 == 3)
                    sum = sum + 8;
                else if (posFromRight % 4 == 0)
                    sum = sum + 6;
            }
        }
        // Check if the sum is divisible by 10, if yes return 1
        if (sum % 10 == 0) return 1;
        // If not divisible by 10, return 0
        return 0;
    }
}

