            // If the current digit is 1, calculate its contribution
            // to the sum based on its position from the right.
            if (bin[i] === '1') {
                let posFromRight = n - i - 1;
                switch (posFromRight % 4) {
                    case 1:
                        sum += 2;
                        break;
                    case 2:
                        sum += 4;
                        break;
                    case 3:
                        sum += 8;
                        break;
                    default:
                        sum += 6;
                        break;
                }
            }
        }

        // Checking if the sum of digits is divisible by 10.
        // If yes, then the binary number is divisible by 10.
        if (sum % 10 === 0) {
            return 1;
        }
        return 0;
    }
