//Back-end complete function template for C++

class Solution{   
public:
    //Function to check if the given string is divisible by 36.
    int checkDivisible36(string S){

        int old = 0;
        //Iterating over the characters of the string.
        for(int i=0;i<S.size();i++)
        {
                //Calculating the remainder when the old value is multiplied by 10 and the current character value is added.
                old = (old*10 + S[i]-'0')%36;
        }

        //Checking if the remainder is 0 and returning the result.
        if(old==0)
        return 1;
        else
        return 0;
    }
};
