class Solution
{
    //Complete this function
    //Function to check whether a number is divisible by 4 or not.
    public int divisibleBy4(string N)
    {
        int num = 0;
        int len = N.Length;

        // if length is 1, we simply check the digit's divisibility
        if (len == 1)
        {
            num = int.Parse(N);
            return (num % 4 == 0) ? 1 : 0;
        }

        // computing the number formed by the last 2 digits
        num = int.Parse(N.Substring(len - 2));
        return (num % 4 == 0) ? 1 : 0;
    }
}

