//Back-end complete function template for C++

class Solution{   
public:
    //Function to check if the given number is divisible by 5 or not.
    int divisibleBy5(string N){

        int old = 0;
        //Iterating through each character in the string.
        for(int i=0;i<N.size();i++)
        {
            //Calculating the remainder when the current digit is added to
            //the previous digits multiplied by 10 and divided by 5.
            old = (old*10 + N[i]-'0')%5;
        }

        //If the remainder is 0, the number is divisible by 5, so return 1.
        if(old==0)
            return 1;
        //If the remainder is not 0, the number is not divisible by 5, so return 0.
        else
            return 0;

    }
};

