// Function to check if two pairs of numbers have the same greatest common divisor

class Solution {
  public:
    int isPossible(int x, int y, int a, int b) {

        // Using the gcd function to compute the greatest common divisor of both pairs
        // If they are equal, return 1; otherwise, return 0
        if(__gcd(abs(x),abs(y)) == __gcd(abs(a),abs(b)))
            return 1;
        else
            return 0;
    }
};

class Solution {
    public int isPossible(int x, int y, int a, int b) {

        // Using the gcd function to compute the greatest common divisor of both pairs
        // If they are equal, return 1; otherwise, return 0
        if(gcd(Math.Abs(x), Math.Abs(y)) == gcd(Math.Abs(a), Math.Abs(b)))
            return 1;
        else
            return 0;
    }

    // Function to compute the greatest common divisor using Euclid's algorithm
    private int gcd(int a, int b) {
        if(b == 0)
            return a;
        else
            return gcd(b, a % b);
    }
}
