//Back-end complete function Template for C++
class Solution {
  public:
    // This function returns true if n is 
// perfect square, else false 
long long int isPerfectSquare(long long int n) { 
    for (int i = 1; i * i <= n; i++) {
 
        // If (i * i = n)
        if ((n % i == 0) && (n / i == i)) {
            return 1;
        }
    }
    return 0;
} 
};
