//Back-end complete function template for C++

class Solution{   
public:
    int checkPerfectSquare(int N){
        // sum is sum of all odd numbers. i is
        // used one by one hold odd numbers
        for (int sum=0,i=1; sum<N; i+=2)
        {
            sum += i;
            if (sum == N)
                return 1;
        }
        return 0;
        
    }
};
