#include <stdbool.h>
#include <stdio.h>

int main() {
    int n;
    scanf("%d", &n);
    // code here
    bool ans = true;

    if (n <= 1) {
        ans = false;
    }

    for (int i = 2; i < n; i++) {
        if (n % i == 0) {
            ans = false;
            break; // Optimization: Exit loop early if not prime
        }
    }

    if (ans == true) {
        printf("True\n");
    } else {
        printf("False\n");
    }

    return 0;
}

