public static boolean checkAscending(ArrayList<Integer> list) {

    int previous = Integer.MIN_VALUE;

    Iterator<Integer> itr = list.iterator();

    while (itr.hasNext()) {
        int next = itr.next();
        if (next < previous) return false;
        previous = next;
    }
    return true;
}

public static boolean checkDescending(ArrayList<Integer> list) {
    int previous = Integer.MAX_VALUE;

    Iterator<Integer> itr = list.iterator();

    while (itr.hasNext()) {
        int next = itr.next();
        if (next > previous) return false;
        previous = next;
    }
    return true;
}

public static boolean isSorted(ArrayList<Integer> list) {

    boolean ascending = checkAscending(list);
    boolean descending = checkDescending(list);

    return ascending || descending;
}
