//Back-end complete function Template for C++

int minDiff(vector<int> &arr, int k) {

    int n = arr.size();

    sort(arr.begin(), arr.end());

    int res = arr[k - 1] - arr[0];

    for (int i = 0; (i + k - 1) < n; i++)
        res = min(res, (arr[i + k - 1] - arr[i]));

    return res;
}
