class Solution
{
    public:
        int closing (string s, int pos)
        {
        	int n = s.length ();
        
        	int cnt = 0;
        	for (int i = pos; i < n; ++i)
        	{
        		if (s[i] == '[') cnt++;
        		else if (s[i] == ']') cnt--;
        		if (cnt == 0)
        			return i;
        	}
        	return -1;
        }
};

