class Sol
{
    // Function to find the closing bracket in a string starting from the given position
    int closing (String s, int pos)
    {
        int n = s.length ();

        int cnt = 0;
        for (int i = pos; i < n; ++i)
        {
            // Increment the count if the character is '['
            if (s.charAt(i) == '[') cnt++;
            // Decrement the count if the character is ']'
            else if (s.charAt(i) == ']') cnt--;
            // Check if the count is zero, indicating that the closing bracket is found
            if (cnt == 0)
                // Return the index of the closing bracket
                return i;
        }
        // If the closing bracket is not found, return -1
        return -1;
    }

}
