class Solution:
    def closing (self,s, pos):
        cnt = 0
        
        #iterating over the string starting from the given position
        for i in range (pos, len (s)):
            
            #incrementing counter if "[" is encountered
            if s[i] == '[':
                cnt += 1
            
            #decrementing counter if "]" is encountered
            elif s[i] == ']':
                cnt -= 1
            
            #if the counter becomes 0, it means the closing bracket for the given position has been found
            if cnt == 0:
                return i
        
        #if no closing bracket is found, return -1
        return -1
    
