class Solution {
    closing(s, pos){
        let n = s.length;
        let cnt = 0;
        for (let i = pos; i < n; i++) {
            if (s[i] === '[') cnt++;
            else if (s[i] === ']') cnt--;
            if (cnt === 0) return i;
        }
        return -1;
    }
}
