//Back-end complete function template in C++

class Solution{
public:
    //Function to find the distribution of coins in the game.
    vector<int> coinsGame(int N, int K){
        vector<int> v;
        //calculating the number of coins for the first player.
        int ans=N-(K-1)/2;
        v.push_back(ans);
        //iterating from 1 to K to determine the distribution of remaining coins.
        for(int i=1;i<K;i++)
        {
            //if i is odd, the player gets 0 coins.
            if(i%2)
                v.push_back(0);
            //if i is even, the player gets 1 coin.
            else
                v.push_back(1);
        }
        //returning the vector containing the distribution of coins.
        return v;
    }
};
